import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import './enter.css';

function Lawyerenter() {
  const navigate = useNavigate();
  const { userId } = useParams();

  const [lawyer, setLawyer] = useState(null); // use `null` instead of an empty array for consistency.
  const [appointments, setAppointments] = useState([]);
  const [activeComponent, setActiveComponent] = useState("ComponentA");

  const showComponent = (component) => {
    setActiveComponent(component);
  };

  useEffect(() => {
    const fetchLawyerData = async () => {
      try {
        const lawyerResponse = await axios.get(`http://localhost:7000/lawyeruid?userId=${userId}`);
        setLawyer(lawyerResponse.data);

        if (lawyerResponse.data._id) {  // only call appointments API if lawyer ID is present
          const appointmentResponse = await axios.get(`http://localhost:7000/appoinmentval?lawyerId=${lawyerResponse.data._id}`);
          setAppointments(appointmentResponse.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchLawyerData();
  }, [userId]);

  return (
    <div>
      <div className="enter-out">
        <div className="welcsd">
          <div className="card_container12">
            <div className="card12">
              <div className="title12">
                <span>Hi, {lawyer?.name || "Loading..."}</span>
              </div>
            </div>
          </div>
          <div className="tools0">
            <div className="tools1">
              <button onClick={() => showComponent("ComponentA")}>Public Profile</button>
              <button onClick={() => showComponent("ComponentB")}>Account</button>
              <button onClick={() => showComponent("ComponentC")}>Following</button>
              <button onClick={() => showComponent("ComponentD")}>Notifications</button>
            </div>
          </div>
          <div className="component-container">
            {activeComponent === "ComponentA" && <ComponentA />}
            {activeComponent === "ComponentB" && <ComponentB />}
            {activeComponent === "ComponentC" && <ComponentC />}
            {activeComponent === "ComponentD" && <ComponentD />}
          </div>
        </div>
        <div>
          <h1>Welcome</h1>
        </div>
        <div className="card6">
          <div className="card6__avatar">
            {lawyer && <img src={`http://localhost:7000/static/${lawyer.originalname}`} alt="Lawyer" style={{ maxWidth: '80px', maxHeight: '80px' }} />}
          </div>
          <div className="card6__title">{lawyer?.name || "Loading..."}</div>
          <div className="card6__subtitle">{lawyer?.age || ""}</div>
          <div className="card6__subtitle"><p>Lawyer</p></div>
          <div className="card6__wrapper">
            <button className="but" onClick={() => navigate(`/lawyerhome/lawyeredite/${lawyer?._id}`)}>Edit profile</button>
          </div>
        </div>
      </div>
    </div>
  );
}

function ComponentA() {
  return <div>Public Profile Component</div>;
}

function ComponentB() {
  return <div>Account Component</div>;
}

function ComponentC() {
  return <div>Following Component</div>;
}

function ComponentD() {
  return <div>Notifications Component</div>;
}

export default Lawyerenter;
